import { mergeProps } from '@zag-js/solid';
import { createSplitProps } from '../../utils/create-split-props';
import { ark } from '../factory';
import { useAvatar } from './use-avatar';
import { AvatarProvider } from './use-avatar-context';
export const AvatarRoot = (props) => {
    const [useAvatarProps, localProps] = createSplitProps()(props, [
        'id',
        'ids',
        'onStatusChange',
    ]);
    const context = useAvatar(useAvatarProps);
    const mergedProps = mergeProps(() => context().getRootProps(), localProps);
    return (<AvatarProvider value={context}>
      <ark.div {...mergedProps}/>
    </AvatarProvider>);
};
