import { mergeProps } from '@zag-js/solid';
import { createSplitProps } from '../../utils/create-split-props';
import { ark } from '../factory';
import { CollapsibleProvider } from './use-collapsible-context';
export const CollapsibleRootProvider = (props) => {
    const [{ value: collapsible }, localProps] = createSplitProps()(props, [
        'value',
    ]);
    const mergedProps = mergeProps(() => collapsible().getRootProps(), localProps);
    return (<CollapsibleProvider value={collapsible}>
      <ark.div {...mergedProps}/>
    </CollapsibleProvider>);
};
