import { mergeProps } from '@zag-js/solid';
import { createMemo } from 'solid-js';
import { createSplitProps } from '../../utils/create-split-props';
import { ark } from '../factory';
import { useMenuContext } from './use-menu-context';
import { MenuItemProvider } from './use-menu-item-context';
export const MenuItem = (props) => {
    const [itemProps, localProps] = createSplitProps()(props, [
        'closeOnSelect',
        'disabled',
        'value',
        'valueText',
    ]);
    const context = useMenuContext();
    const mergedProps = mergeProps(() => context().getItemProps(itemProps), localProps);
    const itemState = createMemo(() => context().getItemState(itemProps));
    return (<MenuItemProvider value={itemState}>
      <ark.div {...mergedProps}/>
    </MenuItemProvider>);
};
