import { mergeProps } from '@zag-js/solid';
import { createUniqueId } from 'solid-js';
import { createSplitProps } from '../../utils/create-split-props';
import { ark } from '../factory';
import { useMenuContext } from './use-menu-context';
import { MenuItemGroupProvider } from './use-menu-item-group-context';
export const MenuRadioItemGroup = (props) => {
    const [optionalItemGroupProps, localProps] = createSplitProps()(props, ['id', 'onValueChange', 'value']);
    const context = useMenuContext();
    const itemGroupProps = mergeProps({ id: createUniqueId() }, optionalItemGroupProps);
    const mergedProps = mergeProps(() => context().getItemGroupProps(itemGroupProps), localProps);
    return (<MenuItemGroupProvider value={itemGroupProps}>
      <ark.div {...mergedProps}/>
    </MenuItemGroupProvider>);
};
