import { mergeProps } from '@zag-js/solid';
import { createSplitProps } from '../../utils/create-split-props';
import { ark } from '../factory';
import { NumberInputProvider } from './use-number-input-context';
export const NumberInputRootProvider = (props) => {
    const [{ value: numberInput }, localProps] = createSplitProps()(props, [
        'value',
    ]);
    const mergedProps = mergeProps(() => numberInput().getRootProps(), localProps);
    return (<NumberInputProvider value={numberInput}>
      <ark.div {...mergedProps}/>
    </NumberInputProvider>);
};
