import { mergeProps } from '@zag-js/solid';
import { createSplitProps } from '../../utils/create-split-props';
import { ark } from '../factory';
import { usePinInput } from './use-pin-input';
import { PinInputProvider } from './use-pin-input-context';
export const PinInputRoot = (props) => {
    const [usePinInputProps, localProps] = createSplitProps()(props, [
        'autoFocus',
        'blurOnComplete',
        'defaultValue',
        'disabled',
        'form',
        'id',
        'ids',
        'invalid',
        'mask',
        'name',
        'onValueChange',
        'onValueComplete',
        'onValueInvalid',
        'otp',
        'pattern',
        'placeholder',
        'readOnly',
        'required',
        'selectOnFocus',
        'translations',
        'type',
        'value',
    ]);
    const pinInput = usePinInput(usePinInputProps);
    const mergedProps = mergeProps(() => pinInput().getRootProps(), localProps);
    return (<PinInputProvider value={pinInput}>
      <ark.div {...mergedProps}/>
    </PinInputProvider>);
};
