import { mergeProps } from '@zag-js/solid';
import { createSplitProps } from '../../utils/create-split-props';
import { ark } from '../factory';
import { RatingGroupProvider } from './use-rating-group-context';
export const RatingGroupRootProvider = (props) => {
    const [{ value: ratingGroup }, localProps] = createSplitProps()(props, [
        'value',
    ]);
    const mergedProps = mergeProps(() => ratingGroup().getRootProps(), localProps);
    return (<RatingGroupProvider value={ratingGroup}>
      <ark.div {...mergedProps}/>
    </RatingGroupProvider>);
};
