import { mergeProps } from '@zag-js/solid';
import { createSplitProps } from '../../utils/create-split-props';
import { RenderStrategyProvider, splitRenderStrategyProps, } from '../../utils/render-strategy';
import { ark } from '../factory';
import { TabsProvider } from './use-tabs-context';
export const TabsRootProvider = (props) => {
    const [renderStrategyProps, tabsProps] = splitRenderStrategyProps(props);
    const [{ value: tabs }, localprops] = createSplitProps()(tabsProps, ['value']);
    const mergedProps = mergeProps(() => tabs().getRootProps(), localprops);
    return (<TabsProvider value={tabs}>
      <RenderStrategyProvider value={renderStrategyProps}>
        <ark.div {...mergedProps}/>
      </RenderStrategyProvider>
    </TabsProvider>);
};
