import { mergeProps } from '@zag-js/solid';
import { createSplitProps } from '../../utils/create-split-props';
import { ark } from '../factory';
import { ToggleGroupProvider } from './use-toggle-group-context';
export const ToggleGroupRootProvider = (props) => {
    const [{ value: toggleGroup }, localProps] = createSplitProps()(props, [
        'value',
    ]);
    const mergedProps = mergeProps(() => toggleGroup().getRootProps(), localProps);
    return (<ToggleGroupProvider value={toggleGroup}>
      <ark.div {...mergedProps}/>
    </ToggleGroupProvider>);
};
