import { mergeProps } from '@zag-js/solid';
import { createSplitProps } from '../../utils/create-split-props';
import { PresenceProvider, splitPresenceProps, usePresence, } from '../presence';
import { useTooltip } from './use-tooltip';
import { TooltipProvider } from './use-tooltip-context';
export const TooltipRoot = (props) => {
    const [presenceProps, tooltipProps] = splitPresenceProps(props);
    const [useTooltipProps, localProps] = createSplitProps()(tooltipProps, [
        'aria-label',
        'closeDelay',
        'closeOnClick',
        'closeOnEscape',
        'closeOnPointerDown',
        'closeOnScroll',
        'defaultOpen',
        'disabled',
        'id',
        'ids',
        'interactive',
        'onOpenChange',
        'open',
        'openDelay',
        'positioning',
    ]);
    const api = useTooltip(useTooltipProps);
    const apiPresence = usePresence(mergeProps(presenceProps, () => ({ present: api().open })));
    return (<TooltipProvider value={api}>
      <PresenceProvider value={apiPresence}>{localProps.children}</PresenceProvider>
    </TooltipProvider>);
};
