import { callHandler } from "@kobalte/utils";
import { splitProps } from "solid-js";
import * as Button from "../button";
import { usePopoverContext } from "./popover-context";
/**
 * The button that closes the popover.
 */
export function PopoverCloseButton(props) {
    const context = usePopoverContext();
    const [local, others] = splitProps(props, ["aria-label", "onClick"]);
    const onClick = (e) => {
        callHandler(e, local.onClick);
        context.close();
    };
    return (<Button.Root aria-label={local["aria-label"] || context.translations().dismiss} onClick={onClick} {...context.dataset()} {...others}/>);
}
