export const matchByFirstChild = ({ parent, matchEl, }) => {
    if (parent === matchEl)
        return true;
    const query = (el) => {
        if (!el)
            return false;
        const child = el.children[0];
        if (child === matchEl) {
            return true;
        }
        return query(child);
    };
    return query(parent);
};
