import { dismissStack } from "../global/dismissStack";
export const runToggleScrollbar = (state, open) => {
    const { onToggleScrollbar, removeScrollbar } = state;
    if (onToggleScrollbar) {
        if (open) {
            if (dismissStack.length > 1)
                return;
            onToggleScrollbar.onRemove();
        }
        else {
            if (dismissStack.length)
                return;
            onToggleScrollbar.onRestore();
        }
        return;
    }
    if (!removeScrollbar)
        return;
    if (dismissStack.length > 1)
        return;
    const el = document.scrollingElement;
    if (open) {
        el.style.overflow = "hidden";
    }
    else {
        el.style.overflow = "";
    }
};
