import { mergeProps } from '@zag-js/solid';
import { createSplitProps } from '../../utils/create-split-props';
import { RenderStrategyProvider, splitRenderStrategyProps, } from '../../utils/render-strategy';
import { ark } from '../factory';
import { AccordionProvider } from './use-accordion-context';
export const AccordionRootProvider = (props) => {
    const [renderStrategyProps, accordionProps] = splitRenderStrategyProps(props);
    const [{ value: accordion }, localProps] = createSplitProps()(accordionProps, [
        'value',
    ]);
    const mergedProps = mergeProps(() => accordion().getRootProps(), localProps);
    return (<AccordionProvider value={accordion}>
      <RenderStrategyProvider value={renderStrategyProps}>
        <ark.div {...mergedProps}/>
      </RenderStrategyProvider>
    </AccordionProvider>);
};
