import { mergeProps } from '@zag-js/solid';
import { createSplitProps } from '../../utils/create-split-props';
import { ark } from '../factory';
import { useCollapsible } from './use-collapsible';
import { CollapsibleProvider } from './use-collapsible-context';
export const CollapsibleRoot = (props) => {
    const [useCollapsibleProps, localProps] = createSplitProps()(props, [
        'defaultOpen',
        'disabled',
        'id',
        'ids',
        'lazyMount',
        'onExitComplete',
        'onOpenChange',
        'open',
        'unmountOnExit',
    ]);
    const api = useCollapsible(useCollapsibleProps);
    const mergedProps = mergeProps(() => api().getRootProps(), localProps);
    return (<CollapsibleProvider value={api}>
      <ark.div {...mergedProps}/>
    </CollapsibleProvider>);
};
