import { mergeProps } from '@zag-js/solid';
import { createSplitProps } from '../../utils/create-split-props';
import { ark } from '../factory';
import { PresenceProvider, splitPresenceProps, usePresence, } from '../presence';
import { ColorPickerProvider } from './use-color-picker-context';
export const ColorPickerRootProvider = (props) => {
    const [presenceProps, colorPickerProps] = splitPresenceProps(props);
    const [{ value: colorPicker }, localProps] = createSplitProps()(colorPickerProps, ['value']);
    const apiPresence = usePresence(mergeProps(presenceProps, () => ({ present: colorPicker().open })));
    const mergedProps = mergeProps(() => colorPicker().getRootProps(), localProps);
    return (<ColorPickerProvider value={colorPicker}>
      <PresenceProvider value={apiPresence}>
        <ark.div {...mergedProps}/>
      </PresenceProvider>
    </ColorPickerProvider>);
};
