import { mergeProps } from '@zag-js/solid';
import { createSplitProps } from '../../utils/create-split-props';
import { ark } from '../factory';
import { useField } from './use-field';
import { FieldProvider } from './use-field-context';
export const FieldRoot = (props) => {
    const [useFieldProps, localProps] = createSplitProps()(props, [
        'id',
        'ids',
        'disabled',
        'invalid',
        'readOnly',
        'required',
    ]);
    const field = useField(useFieldProps);
    const mergedProps = mergeProps(() => field().getRootProps(), localProps);
    return (<FieldProvider value={field}>
      <ark.div {...mergedProps}/>
    </FieldProvider>);
};
