import { mergeProps } from '@zag-js/solid';
import { createSplitProps } from '../../utils/create-split-props';
import { ark } from '../factory';
import { useNumberInput } from './use-number-input';
import { NumberInputProvider } from './use-number-input-context';
export const NumberInputRoot = (props) => {
    const [useNumberInputProps, localProps] = createSplitProps()(props, [
        'allowMouseWheel',
        'allowOverflow',
        'clampValueOnBlur',
        'defaultValue',
        'disabled',
        'focusInputOnChange',
        'form',
        'formatOptions',
        'id',
        'ids',
        'inputMode',
        'invalid',
        'locale',
        'max',
        'min',
        'name',
        'onFocusChange',
        'onValueChange',
        'onValueInvalid',
        'pattern',
        'readOnly',
        'required',
        'spinOnPress',
        'step',
        'translations',
        'value',
    ]);
    const api = useNumberInput(useNumberInputProps);
    const mergedProps = mergeProps(() => api().getRootProps(), localProps);
    return (<NumberInputProvider value={api}>
      <ark.div {...mergedProps}/>
    </NumberInputProvider>);
};
