import { mergeProps } from '@zag-js/solid';
import { createSplitProps } from '../../utils/create-split-props';
import { ark } from '../factory';
import { PinInputProvider } from './use-pin-input-context';
export const PinInputRootProvider = (props) => {
    const [{ value: pinInput }, localProps] = createSplitProps()(props, ['value']);
    const mergedProps = mergeProps(() => pinInput().getRootProps(), localProps);
    return (<PinInputProvider value={pinInput}>
      <ark.div {...mergedProps}/>
    </PinInputProvider>);
};
