import { mergeProps } from '@zag-js/solid';
import { createSplitProps } from '../../utils/create-split-props';
import { ark } from '../factory';
import { useRatingGroup } from './use-rating-group';
import { RatingGroupProvider } from './use-rating-group-context';
export const RatingGroupRoot = (props) => {
    const [useRatingProps, localProps] = createSplitProps()(props, [
        'allowHalf',
        'autoFocus',
        'count',
        'defaultValue',
        'disabled',
        'form',
        'id',
        'ids',
        'name',
        'onHoverChange',
        'onValueChange',
        'readOnly',
        'required',
        'translations',
        'value',
    ]);
    const api = useRatingGroup(useRatingProps);
    const mergedProps = mergeProps(() => api().getRootProps(), localProps);
    return (<RatingGroupProvider value={api}>
      <ark.div {...mergedProps}/>
    </RatingGroupProvider>);
};
