import { mergeProps } from '@zag-js/solid';
import { createSplitProps } from '../../utils/create-split-props';
import { ark } from '../factory';
import { useTagsInput } from './use-tags-input';
import { TagsInputProvider } from './use-tags-input-context';
export const TagsInputRoot = (props) => {
    const [useTagsInputProps, localProps] = createSplitProps()(props, [
        'addOnPaste',
        'allowOverflow',
        'autoFocus',
        'blurBehavior',
        'delimiter',
        'defaultValue',
        'disabled',
        'editable',
        'form',
        'id',
        'ids',
        'inputValue',
        'invalid',
        'max',
        'maxLength',
        'name',
        'onFocusOutside',
        'onHighlightChange',
        'onInputValueChange',
        'onInteractOutside',
        'onPointerDownOutside',
        'onValueChange',
        'onValueInvalid',
        'readOnly',
        'required',
        'translations',
        'validate',
        'value',
    ]);
    const api = useTagsInput(useTagsInputProps);
    const mergedProps = mergeProps(() => api().getRootProps(), localProps);
    return (<TagsInputProvider value={api}>
      <ark.div {...mergedProps}/>
    </TagsInputProvider>);
};
