import { mergeProps } from '@zag-js/solid';
import { createMemo } from 'solid-js';
import { createSplitProps } from '../../utils/create-split-props';
import { ark } from '../factory';
import { useMenuContext } from './use-menu-context';
import { MenuItemProvider } from './use-menu-item-context';
import { MenuOptionItemPropsProvider } from './use-menu-option-item-props-context';
export const MenuCheckboxItem = (props) => {
    const [partialOptionItemProps, localProps] = createSplitProps()(props, [
        'checked',
        'closeOnSelect',
        'disabled',
        'onCheckedChange',
        'value',
        'valueText',
    ]);
    const optionItemProps = mergeProps(partialOptionItemProps, {
        type: 'checkbox',
    });
    const context = useMenuContext();
    const mergedProps = mergeProps(() => context().getOptionItemProps(optionItemProps), localProps);
    const optionItemState = createMemo(() => context().getItemState(optionItemProps));
    return (<MenuOptionItemPropsProvider value={optionItemProps}>
      <MenuItemProvider value={optionItemState}>
        <ark.div {...mergedProps}/>
      </MenuItemProvider>
    </MenuOptionItemPropsProvider>);
};
