import { mergeProps } from '@zag-js/solid';
import { createSplitProps } from '../../utils/create-split-props';
import { ark } from '../factory';
import { PaginationProvider } from './use-pagination-context';
export const PaginationRootProvider = (props) => {
    const [{ value: pagination }, localProps] = createSplitProps()(props, [
        'value',
    ]);
    const mergedProps = mergeProps(() => pagination().getRootProps(), localProps);
    return (<PaginationProvider value={pagination}>
      <ark.nav {...mergedProps}/>
    </PaginationProvider>);
};
