import { mergeProps } from '@zag-js/solid';
import { createSplitProps } from '../../utils/create-split-props';
import { ark } from '../factory';
import { useToggleGroup } from './use-toggle-group';
import { ToggleGroupProvider } from './use-toggle-group-context';
export const ToggleGroupRoot = (props) => {
    const [useToggleGroupProps, restProps] = createSplitProps()(props, [
        'defaultValue',
        'disabled',
        'id',
        'ids',
        'loopFocus',
        'multiple',
        'onValueChange',
        'orientation',
        'rovingFocus',
        'value',
    ]);
    const api = useToggleGroup(useToggleGroupProps);
    const mergedProps = mergeProps(() => api().getRootProps(), restProps);
    return (<ToggleGroupProvider value={api}>
      <ark.div {...mergedProps}/>
    </ToggleGroupProvider>);
};
